import React, { useState } from "react";

export default function TextForm(props) {
  const handleUpClick = () => {
    console.log("You clicked Uppercase" + text);
    let newText = text.toUpperCase();
    setText(newText);
  };
  const handleLoClick = () => {
    let newText = text.toLowerCase();
    setText(newText);
  };
  const handleItClick = () => {
    let newText = <p className="font-italic">text</p>;
    setText(newText);
  };
  const handleOnChange = (event) => {
    console.log("On change");
    setText(event.target.value);
  };

  const [text, setText] = useState("Enter text here");

  return (
    <>
      <div className Name="container">
        <h1>{props.heading}</h1>
        <div className Name="mb-3">
          <textarea className Name="form-control" value={text}
            onChange={handleOnChange}
            id="myBox"
            rows="15"
            column="15"
          ></textarea>
        </div>
        <button className Name="btn btn-danger mx-2" onClick={handleUpClick}>
          Convert to Uppercase
        </button>
        <button className Name="btn btn-primary mx-2" onClick={handleLoClick}>
          Convert to Lowercase
        </button>
        <button className Name="btn btn-primary mx-2" onClick={handleItClick}>
          Convert to Italic
        </button>
      </div>
      <div className Name="container my-3">
        <h1>Text Summary</h1>
        <p>
          {text.split(" ").length} words and {text.length} characters
        </p>
        <p>{0.008 * text.split(" ").length} minutes to read</p>
        <h2>Preview</h2>
        <p>{text}</p>
      </div>
    </>
  );
}
